        {/* Hidden Canvas for Processing */}
        <canvas ref={canvasRef} className="hidden" />
      </div>
    </div>
  );
}

client/src/tools/image-media/image-resiz.content:
import { useState, useRef, useCallback } from "react";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Image, Upload, Download, RotateCcw, Link2, Info } from "lucide-react";
import { motion } from "framer-motion";
import { useToast } from "@/hooks/use-toast";

interface ImageData {
  file: File;
  url: string;
  width: number;
  height: number;
}

interface ResizeSettings {
  width: number;
  height: number;
  lockAspectRatio: boolean;
  quality: number;
  format: string;
  aspectRatio: string;
}

function ImageResizer() {
  const [originalImage, setOriginalImage] = useState<ImageData | null>(null);
  const [resizedImageUrl, setResizedImageUrl] = useState<string | null>(null);
  const [settings, setSettings] = useState<ResizeSettings>({
    width: 800,
    height: 600,
    lockAspectRatio: true,
    quality: 90,
    format: 'jpeg',
    aspectRatio: 'custom'
  });
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [dragActive, setDragActive] = useState(false);

  const fileInputRef = useRef<HTMLInputElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const { toast } = useToast();

  // Aspect ratio presets
  const aspectRatios = {
    'custom': { label: 'Custom', ratio: null },
    '1:1': { label: 'Square (1:1)', ratio: 1 },
    '4:3': { label: 'Standard (4:3)', ratio: 4/3 },
    '16:9': { label: 'Widescreen (16:9)', ratio: 16/9 },
    '3:2': { label: 'Photo (3:2)', ratio: 3/2 },
    '9:16': { label: 'Portrait (9:16)', ratio: 9/16 }
  };

  /**
   * Handle file selection
   */
  const handleFileSelect = useCallback((file: File) => {
    if (!file.type.startsWith('image/')) {
      setError('Please select a valid image file (JPG, PNG, WEBP, GIF)');
      return;
    }

    if (file.size > 10 * 1024 * 1024) { // 10MB limit
      setError('File size must be less than 10MB');
      return;
    }

    setError(null);
    const url = URL.createObjectURL(file);

    const img = document.createElement('img');
    img.onload = () => {
      setOriginalImage({
        file,
        url,
        width: img.width,
        height: img.height
      });

      // Set initial dimensions
      setSettings(prev => ({
        ...prev,
        width: img.width,
        height: img.height
      }));
    };
    img.src = url;
  }, []);

  /**
   * Handle drag and drop
   */
  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileSelect(e.dataTransfer.files[0]);
    }
  }, [handleFileSelect]);

  /**
   * Handle dimension changes
   */
  const updateDimensions = (width: number, height: number) => {
    if (!originalImage) return;

    if (settings.lockAspectRatio && settings.aspectRatio === 'custom') {
      const aspectRatio = originalImage.width / originalImage.height;
      if (width !== settings.width) {
        height = Math.round(width / aspectRatio);
      } else if (height !== settings.height) {
        width = Math.round(height * aspectRatio);
      }
    }

    setSettings(prev => ({ ...prev, width, height }));
  };

  /**
   * Handle aspect ratio change
   */
  const handleAspectRatioChange = (ratio: string) => {
    if (!originalImage) return;

    setSettings(prev => ({ ...prev, aspectRatio: ratio }));

    if (ratio !== 'custom') {
      const aspectRatioValue = aspectRatios[ratio as keyof typeof aspectRatios].ratio;
      if (aspectRatioValue) {
        const newHeight = Math.round(settings.width / aspectRatioValue);
        setSettings(prev => ({ ...prev, height: newHeight, lockAspectRatio: true }));
      }
    }
  };

  /**
   * Process image resize
   */
  const processImage = useCallback(async () => {
    if (!originalImage || !canvasRef.current) return;

    setIsProcessing(true);
    setError(null);

    try {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      if (!ctx) throw new Error('Could not get canvas context');

      canvas.width = settings.width;
      canvas.height = settings.height;

      const img = document.createElement('img');
      img.onload = () => {
        // Clear canvas
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Draw resized image
        ctx.drawImage(img, 0, 0, settings.width, settings.height);

        // Convert to blob and create URL
        canvas.toBlob((blob) => {
          if (blob) {
            const url = URL.createObjectURL(blob);
            setResizedImageUrl(url);
          }
          setIsProcessing(false);
        }, `image/${settings.format}`, settings.quality / 100);
      };
      img.src = originalImage.url;
    } catch (err) {
      setError('Failed to process image. Please try again.');
      setIsProcessing(false);
    }
  }, [originalImage, settings]);

  /**
   * Download resized image
   */
  const downloadImage = () => {
    if (!resizedImageUrl || !originalImage) return;

    const link = document.createElement('a');
    link.href = resizedImageUrl;
    const originalName = originalImage.file.name.split('.')[0];
    link.download = `${originalName}_resized_${settings.width}x${settings.height}.${settings.format}`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    toast({
      title: "Downloaded!",
      description: "Your resized image has been downloaded successfully",
    });
  };

  /**
   * Reset everything
   */
  const resetTool = () => {
    setOriginalImage(null);
    setResizedImageUrl(null);
    setSettings({
      width: 800,
      height: 600,
      lockAspectRatio: true,
      quality: 90,
      format: 'jpeg',
      aspectRatio: 'custom'
    });
    setError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="w-full max-w-6xl mx-auto space-y-6">
      <canvas ref={canvasRef} className="hidden" />

      {/* Upload Section */}
      <Card className="shadow-lg">
        <CardHeader className="bg-primary/5 border-b">
          <div className="flex items-center gap-3">
            <Image className="h-6 w-6 text-primary" />
            <div>
              <CardTitle className="text-2xl">Image Resizer</CardTitle>
              <CardDescription>
                Upload and resize images with custom dimensions and quality settings
              </CardDescription>
            </div>
          </div>
        </CardHeader>

        <CardContent className="pt-6">
          {!originalImage ? (
            <div
              className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
                dragActive 
                  ? 'border-primary bg-primary/5' 
                  : 'border-muted-foreground/25 hover:border-primary/50'
              }`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <Upload className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
              <h3 className="text-lg font-medium mb-2">Upload Your Image</h3>
              <p className="text-muted-foreground mb-4">
                Drag and drop an image here, or click to select
              </p>
              <Button onClick={() => fileInputRef.current?.click()}>
                Select Image
              </Button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={(e) => e.target.files?.[0] && handleFileSelect(e.target.files[0])}
                className="hidden"
              />
              <p className="text-xs text-muted-foreground mt-4">
                Supports JPG, PNG, WEBP, GIF • Max 10MB
              </p>
            </div>
          ) : (
            <div className="space-y-6">
              {/* Original Image Preview */}
              <div className="grid gap-6 md:grid-cols-2">
                <div>
                  <h3 className="font-medium mb-3">Original Image</h3>
                  <div className="border rounded-lg overflow-hidden bg-muted/50">
                    <img 
                      src={originalImage.url} 
                      alt="Original" 
                      className="w-full h-48 object-contain"
                    />
                    <div className="p-3 text-xs text-muted-foreground">
                      {originalImage.width} × {originalImage.height} px
                    </div>
                  </div>
                </div>

                {/* Resized Image Preview */}
                <div>
                  <h3 className="font-medium mb-3">Preview</h3>
                  <div className="border rounded-lg overflow-hidden bg-muted/50 min-h-[12rem]">
                    {resizedImageUrl ? (
                      <>
                        <img 
                          src={resizedImageUrl} 
                          alt="Resized" 
                          className="w-full h-48 object-contain"
                        />
                        <div className="p-3 text-xs text-muted-foreground">
                          {settings.width} × {settings.height} px
                        </div>
                      </>
                    ) : (
                      <div className="h-48 flex items-center justify-center text-muted-foreground">
                        Click "Resize Image" to see preview
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Resize Settings */}
              <div className="space-y-6">
                <h3 className="font-medium">Resize Settings</h3>

                {/* Aspect Ratio Preset */}
                <div className="grid gap-4 sm:grid-cols-2">
                  <div>
                    <label className="block text-sm font-medium mb-2">Aspect Ratio</label>
                    <Select 
                      value={settings.aspectRatio} 
                      onValueChange={handleAspectRatioChange}
                    >
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {Object.entries(aspectRatios).map(([key, { label }]) => (
                          <SelectItem key={key} value={key}>{label}</SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="flex items-center space-x-2 pt-6">
                    <Switch 
                      checked={settings.lockAspectRatio}
                      onCheckedChange={(checked) => setSettings(prev => ({ ...prev, lockAspectRatio: checked }))}
                    />
                    <div className="flex items-center gap-1">
                      <Link2 className="h-4 w-4" />
                      <span className="text-sm">Lock Aspect Ratio</span>
                    </div>
                  </div>
                </div>

                {/* Dimensions */}
                <div className="grid gap-4 sm:grid-cols-2">
                  <div>
                    <label className="block text-sm font-medium mb-2">Width (px)</label>
                    <Input
                      type="number"
                      value={settings.width}
                      onChange={(e) => updateDimensions(parseInt(e.target.value) || 0, settings.height)}
                      min={1}
                      max={4000}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Height (px)</label>
                    <Input
                      type="number"
                      value={settings.height}
                      onChange={(e) => updateDimensions(settings.width, parseInt(e.target.value) || 0)}
                      min={1}
                      max={4000}
                    />
                  </div>
                </div>

                {/* Output Settings */}
                <div className="grid gap-4 sm:grid-cols-2">
                  <div>
                    <label className="block text-sm font-medium mb-2">Output Format</label>
                    <Select 
                      value={settings.format} 
                      onValueChange={(value) => setSettings(prev => ({ ...prev, format: value }))}
                    >
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="jpeg">JPEG</SelectItem>
                        <SelectItem value="png">PNG</SelectItem>
                        <SelectItem value="webp">WEBP</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-2">Quality: {settings.quality}%</label>
                    <Slider
                      value={[settings.quality]}
                      onValueChange={(value) => setSettings(prev => ({ ...prev, quality: value[0] }))}
                      min={1}
                      max={100}
                      step={1}
                      className="w-full"
                    />
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex gap-3 flex-wrap">
                  <Button 
                    onClick={processImage} 
                    disabled={isProcessing}
                    className="gap-2"
                  >
                    {isProcessing ? 'Processing...' : 'Resize Image'}
                  </Button>

                  {resizedImageUrl && (
                    <Button 
                      onClick={downloadImage}
                      variant="outline"
                      className="gap-2"
                    >
                      <Download className="h-4 w-4" />
                      Download
                    </Button>
                  )}

                  <Button 
                    onClick={resetTool}
                    variant="outline"
                    className="gap-2"
                  >
                    <RotateCcw className="h-4 w-4" />
                    Reset
                  </Button>
                </div>
              </div>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <Alert variant="destructive" className="mt-4">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
        </CardContent>

        <CardFooter className="border-t bg-muted/10 text-xs text-muted-foreground">
          <div className="flex items-center gap-2">
            <Info className="h-4 w-4" />
            All processing happens in your browser - your images never leave your device
          </div>
        </CardFooter>
      </Card>
    </div>
  );
}

export default ImageResizer;

client/src/tools/pdf-document/pdf-editor.content:
import React, { useState, useRef, useCallback, useEffect } from 'react';
import { PDFDocument, rgb } from 'pdf-lib';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Upload, Download, FileText, Type, Image, Highlighter, 
  RotateCw, Trash2, Copy, Plus, Save, MousePointer,
  Bold, Italic, Underline, Palette, Move, Edit3
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Slider } from '@/components/ui/slider';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { useTheme } from '@/lib/ThemeProvider';

interface PDFPage {
  id: string;
  canvas: HTMLCanvasElement;
  width: number;
  height: number;
  scale: number;
}

interface TextElement {
  id: string;
  text: string;
  x: number;
  y: number;
  fontSize: number;
  color: string;
  fontWeight: string;
  fontStyle: string;
  textDecoration: string;
  pageId: string;
}

interface ImageElement {
  id: string;
  src: string;
  x: number;
  y: number;
  width: number;
  height: number;
  pageId: string;
}

const FONT_SIZES = [8, 10, 12, 14, 16, 18, 20, 24, 28, 32, 36];
const COLORS = ['#000000', '#FF0000', '#00FF00', '#0000FF', '#FFFF00', '#FF00FF', '#00FFFF', '#808080'];

interface AnnotationElement {
  id: string;
  type: 'highlight' | 'underline' | 'strikethrough' | 'rectangle' | 'circle';
  x: number;
  y: number;
  width: number;
  height: number;
  color: string;
  pageId: string;
}

interface SignatureElement {
  id: string;
  type: 'draw' | 'upload';
  data: string; // SVG path or image data
  x: number;
  y: number;
  width: number;
  height: number;
  pageId: string;
}

export default function PDFEditor() {
  const { theme } = useTheme();
  const [pdfFile, setPdfFile] = useState<File | null>(null);
  const [pages, setPages] = useState<PDFPage[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [tool, setTool] = useState<'select' | 'text' | 'image' | 'highlight' | 'annotation' | 'signature'>('select');
  const [textElements, setTextElements] = useState<TextElement[]>([]);
  const [imageElements, setImageElements] = useState<ImageElement[]>([]);
  const [annotationElements, setAnnotationElements] = useState<AnnotationElement[]>([]);
  const [signatureElements, setSignatureElements] = useState<SignatureElement[]>([]);
  const [selectedElement, setSelectedElement] = useState<string | null>(null);
  const [zoomLevel, setZoomLevel] = useState(1);
  const [isDrawingSignature, setIsDrawingSignature] = useState(false);
  const [signaturePath, setSignaturePath] = useState<string>('');
  const [editingText, setEditingText] = useState<string | null>(null);
  const [newText, setNewText] = useState('');
  const [fontSize, setFontSize] = useState([14]);
  const [textColor, setTextColor] = useState('#000000');
  const [fontWeight, setFontWeight] = useState('normal');
  const [fontStyle, setFontStyle] = useState('normal');
  const [textDecoration, setTextDecoration] = useState('none');

  const fileInputRef = useRef<HTMLInputElement>(null);
  const imageInputRef = useRef<HTMLInputElement>(null);
  const pdfViewerRef = useRef<HTMLDivElement>(null);

  // Initialize demo PDF pages on mount
  useEffect(() => {
    // Create demo pages for testing
    const createDemoPages = () => {
      const demoPages: PDFPage[] = [];
      for (let i = 1; i <= 3; i++) {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        canvas.width = 600;
        canvas.height = 800;

        if (ctx) {
          // Draw demo page background
          ctx.fillStyle = '#ffffff';
          ctx.fillRect(0, 0, canvas.width, canvas.height);

          // Draw border
          ctx.strokeStyle = '#cccccc';
          ctx.lineWidth = 2;
          ctx.strokeRect(0, 0, canvas.width, canvas.height);

          // Draw demo content
          ctx.fillStyle = '#333333';
          ctx.font = '24px Arial';
          ctx.fillText(`Demo PDF Page ${i}`, 50, 100);

          ctx.font = '16px Arial';
          ctx.fillText('This is a demo PDF page for testing.', 50, 150);
          ctx.fillText('Upload a real PDF to replace this demo.', 50, 180);
          ctx.fillText('You can add text, images, and annotations.', 50, 210);
        }

        demoPages.push({
          id: `demo-page-${i}`,
          canvas,
          width: canvas.width,
          height: canvas.height,
          scale: 1
        });
      }
      return demoPages;
    };

    // Only create demo pages if no PDF is loaded
    if (!pdfFile && pages.length === 0) {
      setPages(createDemoPages());
      setCurrentPage(0);
    }
  }, [pdfFile, pages.length]);

  // Handle PDF file upload
  const handleFileUpload = useCallback(async (file: File) => {
    if (!file.type.includes('pdf')) {
      setError('Please select a valid PDF file');
      return;
    }

    if (file.size > 50 * 1024 * 1024) {
      setError('File size must be less than 50MB');
      return;
    }

    setError(null);
    setLoading(true);
    setPdfFile(file);

    try {
      // For now, create a demo representation of the uploaded PDF
      const pdfPages: PDFPage[] = [];

      // Create pages based on file (simplified approach)
      for (let pageNum = 1; pageNum <= 5; pageNum++) {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        canvas.width = 600;
        canvas.height = 800;

        if (ctx) {
          // Draw page background
          ctx.fillStyle = '#ffffff';
          ctx.fillRect(0, 0, canvas.width, canvas.height);

          // Draw border
          ctx.strokeStyle = '#cccccc';
          ctx.lineWidth = 2;
          ctx.strokeRect(0, 0, canvas.width, canvas.height);

          // Draw file info
          ctx.fillStyle = '#333333';
          ctx.font = '20px Arial';
          ctx.fillText(`PDF: ${file.name}`, 50, 100);
          ctx.font = '16px Arial';
          ctx.fillText(`Page ${pageNum}`, 50, 130);
          ctx.fillText(`Size: ${(file.size / 1024 / 1024).toFixed(2)} MB`, 50, 160);
          ctx.fillText('Click anywhere to add text', 50, 200);
          ctx.fillText('Use toolbar to add images', 50, 230);
        }

        pdfPages.push({
          id: `page-${pageNum}`,
          canvas,
          width: canvas.width,
          height: canvas.height,
          scale: 1
        });
      }

      setPages(pdfPages);
      setCurrentPage(0);
      setLoading(false);
    } catch (err) {
      console.error('PDF processing error:', err);
      setError('Failed to process PDF file. Please try with a different PDF.');
      setLoading(false);
    }
  }, []);

  // Handle drag and drop
  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) handleFileUpload(file);
  }, [handleFileUpload]);

  // Handle page click for adding elements
  const handlePageClick = useCallback((e: React.MouseEvent, pageId: string) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = (e.clientX - rect.left) / zoomLevel;
    const y = (e.clientY - rect.top) / zoomLevel;

    if (tool === 'text') {
      const newTextElement: TextElement = {
        id: `text-${Date.now()}`,
        text: 'New Text',
        x,
        y,
        fontSize: fontSize[0],
        color: textColor,
        fontWeight,
        fontStyle,
        textDecoration,
        pageId
      };

      setTextElements(prev => [...prev, newTextElement]);
      setEditingText(newTextElement.id);
      setNewText(newTextElement.text);
    } else if (tool === 'highlight') {
      const newAnnotation: AnnotationElement = {
        id: `annotation-${Date.now()}`,
        type: 'highlight',
        x,
        y,
        width: 100,
        height: 20,
        color: '#FFFF00',
        pageId
      };
      setAnnotationElements(prev => [...prev, newAnnotation]);
    }
  }, [tool, fontSize, textColor, fontWeight, fontStyle, textDecoration, zoomLevel]);

  // Handle text editing
  const handleTextEdit = useCallback((elementId: string, newTextValue: string) => {
    setTextElements(prev => 
      prev.map(el => 
        el.id === elementId ? { ...el, text: newTextValue } : el
      )
    );
  }, []);

  // Handle zoom
  const handleZoom = useCallback((direction: 'in' | 'out') => {
    setZoomLevel(prev => {
      const newZoom = direction === 'in' ? prev * 1.2 : prev / 1.2;
      return Math.max(0.5, Math.min(3, newZoom));
    });
  }, []);

  // Handle annotation creation
  const handleAnnotationClick = useCallback((e: React.MouseEvent, pageId: string, type: AnnotationElement['type']) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = (e.clientX - rect.left) / zoomLevel;
    const y = (e.clientY - rect.top) / zoomLevel;

    const newAnnotation: AnnotationElement = {
      id: `annotation-${Date.now()}`,
      type,
      x,
      y,
      width: type === 'highlight' ? 100 : 50,
      height: type === 'highlight' ? 20 : 50,
      color: type === 'highlight' ? '#FFFF00' : textColor,
      pageId
    };

    setAnnotationElements(prev => [...prev, newAnnotation]);
  }, [zoomLevel, textColor]);

  // Handle signature drawing
  const handleSignatureStart = useCallback((e: React.MouseEvent) => {
    if (tool === 'signature') {
      setIsDrawingSignature(true);
      const rect = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      setSignaturePath(`M${x},${y}`);
    }
  }, [tool]);

  const handleSignatureMove = useCallback((e: React.MouseEvent) => {
    if (isDrawingSignature) {
      const rect = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      setSignaturePath(prev => prev + ` L${x},${y}`);
    }
  }, [isDrawingSignature]);

  const handleSignatureEnd = useCallback(() => {
    if (isDrawingSignature && signaturePath) {
      const newSignature: SignatureElement = {
        id: `signature-${Date.now()}`,
        type: 'draw',
        data: signaturePath,
        x: 100,
        y: 100,
        width: 200,
        height: 100,
        pageId: pages[currentPage]?.id || '0'
      };

      setSignatureElements(prev => [...prev, newSignature]);
      setIsDrawingSignature(false);
      setSignaturePath('');
    }
  }, [isDrawingSignature, signaturePath, pages, currentPage]);

  // Handle element deletion
  const handleDeleteElement = useCallback((elementId: string) => {
    setTextElements(prev => prev.filter(el => el.id !== elementId));
    setImageElements(prev => prev.filter(el => el.id !== elementId));
    setAnnotationElements(prev => prev.filter(el => el.id !== elementId));
    setSignatureElements(prev => prev.filter(el => el.id !== elementId));
    setSelectedElement(null);
  }, []);

  // Handle image upload
  const handleImageUpload = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file || !file.type.startsWith('image/')) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const newImageElement: ImageElement = {
        id: `image-${Date.now()}`,
        src: event.target?.result as string,
        x: 100,
        y: 100,
        width: 200,
        height: 150,
        pageId: pages[currentPage]?.id || '0'
      };

      setImageElements(prev => [...prev, newImageElement]);
    };
    reader.readAsDataURL(file);
  }, [pages, currentPage]);

  // Handle page operations
  const handlePageRotate = useCallback((pageIndex: number) => {
    setPages(prev => 
      prev.map((page, index) => 
        index === pageIndex ? { ...page, scale: page.scale } : page
      )
    );
  }, []);

  const handlePageDelete = useCallback((pageIndex: number) => {
    if (pages.length <= 1) return;

    setPages(prev => prev.filter((_, index) => index !== pageIndex));
    if (currentPage >= pageIndex && currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  }, [pages.length, currentPage]);

  const handlePageDuplicate = useCallback((pageIndex: number) => {
    const pageToClone = pages[pageIndex];
    if (!pageToClone) return;

    const newPage: PDFPage = {
      ...pageToClone,
      id: `page-${Date.now()}`
    };

    setPages(prev => [...prev.slice(0, pageIndex + 1), newPage, ...prev.slice(pageIndex + 1)]);
  }, [pages]);

  // Export PDF
  const handleExportPDF = useCallback(async () => {
    if (!pdfFile || pages.length === 0) {
      setError('No PDF loaded to export');
      return;
    }

    try {
      setLoading(true);

      const arrayBuffer = await pdfFile.arrayBuffer();
      const pdfDoc = await PDFDocument.load(arrayBuffer);
      const pdfPages = pdfDoc.getPages();

      // Add text elements to PDF
      textElements.forEach(textEl => {
        const pageIndex = pages.findIndex(p => p.id === textEl.pageId);
        if (pageIndex >= 0 && pdfPages[pageIndex]) {
          const page = pdfPages[pageIndex];
          const { width, height } = page.getSize();

          // Convert screen coordinates to PDF coordinates
          const pdfX = (textEl.x / pages[pageIndex].width) * width;
          const pdfY = height - ((textEl.y / pages[pageIndex].height) * height);

          page.drawText(textEl.text, {
            x: pdfX,
            y: pdfY,
            size: textEl.fontSize,
            color: rgb(
              parseInt(textEl.color.slice(1, 3), 16) / 255,
              parseInt(textEl.color.slice(3, 5), 16) / 255,
              parseInt(textEl.color.slice(5, 7), 16) / 255
            ),
          });
        }
      });

      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);

      const a = document.createElement('a');
      a.href = url;
      a.download = 'edited-document.pdf';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      setLoading(false);
    } catch (err) {
      console.error('Export error:', err);
      setError('Failed to export PDF. Please try again.');
      setLoading(false);
    }
  }, [pdfFile, pages, textElements]);

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-8"
        >
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="p-3 bg-gradient-to-r from-red-500 to-orange-500 rounded-xl text-white">
              <FileText className="w-8 h-8" />
            </div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-red-600 to-orange-600 bg-clip-text text-transparent">
              PDF Editor
            </h1>
          </div>
          <p className="text-gray-600 dark:text-gray-300 text-lg max-w-2xl mx-auto">
            Professional PDF editing tool with text editing, image insertion, page management, and annotation features
          </p>
        </motion.div>

        {/* Upload Section */}
        {!pdfFile && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mb-8"
          >
            <Card className="border-0 shadow-xl bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Upload className="w-5 h-5" />
                  Upload PDF Document
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div
                  className={`border-2 border-dashed rounded-xl p-12 text-center transition-all duration-300 cursor-pointer ${
                    theme === 'dark' 
                      ? 'border-gray-600 hover:border-red-400 bg-gray-700/50' 
                      : 'border-gray-300 hover:border-red-400 bg-gray-50'
                  }`}
                  onDrop={handleDrop}
                  onDragOver={(e) => e.preventDefault()}
                  onClick={() => fileInputRef.current?.click()}
                >
                  <Upload className="w-16 h-16 mx-auto mb-4 text-gray-400" />
                  <p className="text-gray-600 dark:text-gray-300 mb-2 text-lg">
                    Drag and drop a PDF file here, or click to select
                  </p>
                  <p className="text-sm text-gray-400">
                    Supports PDF files up to 50MB
                  </p>
                </div>

                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".pdf"
                  onChange={(e) => {
                    const file = e.target.files?.[0];
                    if (file) handleFileUpload(file);
                  }}
                  className="hidden"
                />

                {error && (
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="mt-4 p-4 bg-red-100 dark:bg-red-900/30 border border-red-300 dark:border-red-600 rounded-lg text-red-700 dark:text-red-300"
                  >
                    {error}
                  </motion.div>
                )}
              </CardContent>
            </Card>
          </motion.div>
        )}

        {/* PDF Editor Interface */}
        {pdfFile && (
          <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
            {/* Toolbar */}
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="lg:col-span-1"
            >
              <Card className="border-0 shadow-xl bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm">
                <CardHeader>
                  <CardTitle className="text-lg">Tools</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  {/* Tool Selection */}
                  <div>
                    <label className="block text-sm font-medium mb-2">Select Tool</label>
                    <div className="grid grid-cols-2 gap-2">
                      <Button
                        variant={tool === 'select' ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => setTool('select')}
                        className="flex items-center gap-1"
                      >
                        <MousePointer className="w-4 h-4" />
                        Select
                      </Button>
                      <Button
                        variant={tool === 'text' ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => setTool('text')}
                        className="flex items-center gap-1"
                      >
                        <Type className="w-4 h-4" />
                        Text
                      </Button>
                      <Button
                        variant={tool === 'image' ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => {
                          setTool('image');
                          imageInputRef.current?.click();
                        }}
                        className="flex items-center gap-1"
                      >
                        <Image className="w-4 h-4" />
                        Image
                      </Button>
                      <Button
                        variant={tool === 'highlight' ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => setTool('highlight')}
                        className="flex items-center gap-1"
                      >
                        <Highlighter className="w-4 h-4" />
                        Highlight
                      </Button>
                      <Button
                        variant={tool === 'annotation' ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => setTool('annotation')}
                        className="flex items-center gap-1"
                      >
                        <Edit3 className="w-4 h-4" />
                        Annotate
                      </Button>
                      <Button
                        variant={tool === 'signature' ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => setTool('signature')}
                        className="flex items-center gap-1"
                      >
                        <Edit3 className="w-4 h-4" />
                        Signature
                      </Button>
                    </div>
                  </div>

                  {/* Zoom Controls */}
                  <div>
                    <label className="block text-sm font-medium mb-2">
                      Zoom: {Math.round(zoomLevel * 100)}%
                    </label>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleZoom('out')}
                        className="flex-1"
                      >
                        Zoom Out
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setZoomLevel(1)}
                        className="flex-1"
                      >
                        Reset
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleZoom('in')}
                        className="flex-1"
                      >
                        Zoom In
                      </Button>
                    </div>
                  </div>

                  {/* Annotation Tools */}
                  {tool === 'annotation' && (
                    <div>
                      <label className="block text-sm font-medium mb-2">Annotation Type</label>
                      <div className="grid grid-cols-2 gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleAnnotationClick({ currentTarget: pdfViewerRef.current } as any, pages[currentPage]?.id || '0', 'rectangle')}
                          className="flex items-center gap-1"
                        >
                          Rectangle
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleAnnotationClick({ currentTarget: pdfViewerRef.current } as any, pages[currentPage]?.id || '0', 'circle')}
                          className="flex items-center gap-1"
                        >
                          Circle
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleAnnotationClick({ currentTarget: pdfViewerRef.current } as any, pages[currentPage]?.id || '0', 'underline')}
                          className="flex items-center gap-1"
                        >
                          Underline
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleAnnotationClick({ currentTarget: pdfViewerRef.current } as any, pages[currentPage]?.id || '0', 'strikethrough')}
                          className="flex items-center gap-1"
                        >
                          Strike
                        </Button>
                      </div>
                    </div>
                  )}

                  {/* Text Formatting */}
                  {(tool === 'text' || selectedElement?.startsWith('text')) && (
                    <div className="space-y-3">
                      <div>
                        <label className="block text-sm font-medium mb-2">Font Size</label>
                        <Select value={fontSize[0].toString()} onValueChange={(value) => setFontSize([parseInt(value)])}>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {FONT_SIZES.map((size) => (
                              <SelectItem key={size} value={size.toString()}>
                                {size}px
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>

                      <div>
                        <label className="block text-sm font-medium mb-2">Text Color</label>
                        <div className="grid grid-cols-4 gap-2">
                          {COLORS.map((color) => (
                            <button
                              key={color}
                              className={`w-8 h-8 rounded border-2 ${textColor === color ? 'border-blue-500' : 'border-gray-300'}`}
                              style={{ backgroundColor: color }}
                              onClick={() => setTextColor(color)}
                            />
                          ))}
                        </div>
                      </div>

                      <div>
                        <label className="block text-sm font-medium mb-2">Text Style</label>
                        <div className="flex gap-2">
                          <Button
                            variant={fontWeight === 'bold' ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setFontWeight(fontWeight === 'bold' ? 'normal' : 'bold')}
                          >
                            <Bold className="w-4 h-4" />
                          </Button>
                          <Button
                            variant={fontStyle === 'italic' ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setFontStyle(fontStyle === 'italic' ? 'normal' : 'italic')}
                          >
                            <Italic className="w-4 h-4" />
                          </Button>
                          <Button
                            variant={textDecoration === 'underline' ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setTextDecoration(textDecoration === 'underline' ? 'none' : 'underline')}
                          >
                            <Underline className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Page Management */}
                  <div>
                    <label className="block text-sm font-medium mb-2">Page Actions</label>
                    <div className="grid grid-cols-2 gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handlePageRotate(currentPage)}
                        className="flex items-center gap-1"
                      >
                        <RotateCw className="w-4 h-4" />
                        Rotate
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handlePageDuplicate(currentPage)}
                        className="flex items-center gap-1"
                      >
                        <Copy className="w-4 h-4" />
                        Duplicate
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handlePageDelete(currentPage)}
                        className="flex items-center gap-1"
                        disabled={pages.length <= 1}
                      >
                        <Trash2 className="w-4 h-4" />
                        Delete
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        className="flex items-center gap-1"
                      >
                        <Plus className="w-4 h-4" />
                        Add Page
                      </Button>
                    </div>
                  </div>

                  {/* Export */}
                  <div>
                    <Button
                      onClick={handleExportPDF}
                      className="w-full bg-gradient-to-r from-red-500 to-orange-500 hover:from-red-600 hover:to-orange-600 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-300 shadow-lg hover:shadow-xl"
                    >
                      <Download className="w-4 h-4 mr-2" />
                      Export PDF
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </motion.div>

            {/* PDF Viewer */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 }}
              className="lg:col-span-3"
            >
              <Card className="border-0 shadow-xl bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm h-full">
                <CardContent className="p-6">
                  <div
                    ref={pdfViewerRef}
                    className="relative w-full min-h-[600px] bg-gray-100 dark:bg-gray-700 rounded-lg border-2 border-dashed border-gray-300 dark:border-gray-600"
                    onClick={(e) => handlePageClick(e, pages[currentPage]?.id || '0')}
                  >
                    {loading ? (
                      <div className="flex items-center justify-center h-full">
                        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-red-500"></div>
                        <p className="ml-4 text-gray-600 dark:text-gray-300">Processing PDF...</p>
                      </div>
                    ) : pages.length > 0 ? (
                      <div className="flex flex-col items-center p-4">
                        {/* PDF Page Canvas */}
                        <div 
                          className="relative bg-white shadow-lg"
                          style={{ transform: `scale(${zoomLevel})`, transformOrigin: 'top left' }}
                          onMouseDown={tool === 'signature' ? handleSignatureStart : undefined}
                          onMouseMove={tool === 'signature' ? handleSignatureMove : undefined}
                          onMouseUp={tool === 'signature' ? handleSignatureEnd : undefined}
                        >
                          <canvas
                            ref={(canvas) => {
                              if (canvas && pages[currentPage]) {
                                const ctx = canvas.getContext('2d');
                                if (ctx) {
                                  canvas.width = pages[currentPage].width;
                                  canvas.height = pages[currentPage].height;
                                  ctx.drawImage(pages[currentPage].canvas, 0, 0);
                                }
                              }
                            }}
                            className="max-w-full h-auto border border-gray-300"
                          />

                          {/* Current signature drawing */}
                          {isDrawingSignature && signaturePath && (
                            <svg 
                              className="absolute inset-0 pointer-events-none"
                              style={{ width: pages[currentPage]?.width, height: pages[currentPage]?.height }}
                            >
                              <path 
                                d={signaturePath} 
                                stroke="#000000" 
                                strokeWidth="2" 
                                fill="none"
                              />
                            </svg>
                          )}

                          {/* Text elements overlay */}
                          {textElements
                            .filter(el => el.pageId === pages[currentPage]?.id)
                            .map(element => (
                              <div
                                key={element.id}
                                className={`absolute cursor-pointer ${
                                  selectedElement === element.id ? 'ring-2 ring-blue-500' : ''
                                }`}
                                style={{
                                  left: element.x,
                                  top: element.y,
                                  fontSize: element.fontSize,
                                  color: element.color,
                                  fontWeight: element.fontWeight,
                                  fontStyle: element.fontStyle,
                                  textDecoration: element.textDecoration,
                                }}
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setSelectedElement(element.id);
                                }}
                                onDoubleClick={() => {
                                  setEditingText(element.id);
                                  setNewText(element.text);
                                }}
                              >
                                {editingText === element.id ? (
                                  <Input
                                    value={newText}
                                    onChange={(e) => setNewText(e.target.value)}
                                    onBlur={() => {
                                      handleTextEdit(element.id, newText);
                                      setEditingText(null);
                                    }}
                                    onKeyPress={(e) => {
                                      if (e.key === 'Enter') {
                                        handleTextEdit(element.id, newText);
                                        setEditingText(null);
                                      }
                                    }}
                                    className="text-inherit bg-transparent border-none p-0 focus:ring-0"
                                    autoFocus
                                  />
                                ) : (
                                  element.text
                                )}
                              </div>
                            ))}

                          {/* Image elements overlay */}
                          {imageElements
                            .filter(el => el.pageId === pages[currentPage]?.id)
                            .map(element => (
                              <div
                                key={element.id}
                                className={`absolute cursor-move ${
                                  selectedElement === element.id ? 'ring-2 ring-blue-500' : ''
                                }`}
                                style={{
                                  left: element.x,
                                  top: element.y,
                                  width: element.width,
                                  height: element.height,
                                }}
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setSelectedElement(element.id);
                                }}
                              >
                                <img
                                  src={element.src}
                                  alt="PDF Element"
                                  className="w-full h-full object-contain"
                                />
                              </div>
                            ))}

                          {/* Annotation elements overlay */}
                          {annotationElements
                            .filter(el => el.pageId === pages[currentPage]?.id)
                            .map(element => (
                              <div
                                key={element.id}
                                className={`absolute cursor-pointer ${
                                  selectedElement === element.id ? 'ring-2 ring-blue-500' : ''
                                }`}
                                style={{
                                  left: element.x,
                                  top: element.y,
                                  width: element.width,
                                  height: element.height,
                                  backgroundColor: element.type === 'highlight' ? element.color : 'transparent',
                                  border: element.type === 'rectangle' ? `2px solid ${element.color}` : 'none',
                                  borderRadius: element.type === 'circle' ? '50%' : '0',
                                  borderBottom: element.type === 'underline' ? `2px solid ${element.color}` : 'none',
                                  textDecoration: element.type === 'strikethrough' ? 'line-through' : 'none',
                                  opacity: element.type === 'highlight' ? 0.5 : 1,
                                }}
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setSelectedElement(element.id);
                                }}
                              />
                            ))}

                          {/* Signature elements overlay */}
                          {signatureElements
                            .filter(el => el.pageId === pages[currentPage]?.id)
                            .map(element => (
                              <div
                                key={element.id}
                                className={`absolute cursor-move ${
                                  selectedElement === element.id ? 'ring-2 ring-blue-500' : ''
                                }`}
                                style={{
                                  left: element.x,
                                  top: element.y,
                                  width: element.width,
                                  height: element.height,
                                }}
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setSelectedElement(element.id);
                                }}
                              >
                                {element.type === 'draw' ? (
                                  <svg width="100%" height="100%" viewBox="0 0 200 100">
                                    <path 
                                      d={element.data} 
                                      stroke="#000000" 
                                      strokeWidth="2" 
                                      fill="none"
                                    />
                                  </svg>
                                ) : (
                                  <img
                                    src={element.data}
                                    alt="Signature"
                                    className="w-full h-full object-contain"
                                  />
                                )}
                              </div>
                            ))}
                        </div>

                        {/* Page info */}
                        <div className="mt-4">
                          <Badge variant="secondary">
                            Page {currentPage + 1} of {pages.length}
                          </Badge>
                        </div>
                      </div>
                    ) : (
                      <div className="flex flex-col items-center justify-center h-full text-gray-500">
                        <FileText className="w-24 h-24 mb-4" />
                        <p className="text-lg mb-2">PDF Preview Area</p>
                        <p className="text-sm">
                          {error ? error : 'Upload a PDF file to start editing'}
                        </p>
                      </div>
                    )}

                    {/* Delete button for selected element */}
                    {selectedElement && pages.length > 0 && (
                      <Button
                        variant="destructive"
                        size="sm"
                        className="absolute top-2 right-2 z-10"
                        onClick={() => handleDeleteElement(selectedElement)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    )}
                  </div>

                  {/* Page Navigation */}
                  {pages.length > 1 && (
                    <div className="flex justify-center items-center gap-4 mt-4">
                      <Button
                        variant="outline"
                        onClick={() => setCurrentPage(Math.max(0, currentPage - 1))}
                        disabled={currentPage === 0}
                      >
                        Previous
                      </Button>
                      <span className="text-sm text-gray-600 dark:text-gray-300">
                        Page {currentPage + 1} of {pages.length}
                      </span>
                      <Button
                        variant="outline"
                        onClick={() => setCurrentPage(Math.min(pages.length - 1, currentPage + 1))}
                        disabled={currentPage === pages.length - 1}
                      >
                        Next
                      </Button>
                    </div>
                  )}
                </CardContent>
              </Card>
            </motion.div>
          </div>
        )}

        {/* Hidden file inputs */}
        <input
          ref={imageInputRef}
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="hidden"
        />

        {/* Tool Information Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="mt-8"
        >
          <Card className="border-0 shadow-xl bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm">
            <CardHeader>
              <CardTitle className="text-2xl font-bold">About PDF Editor Tool</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* What is this tool */}
              <div>
