};

// Unit display names with abbreviations
const unitLabels = {
  "km-per-liter": "Kilometers per liter (km/L)",
  "miles-per-gallon-us": "Miles per gallon US (MPG)",
  "miles-per-gallon-imperial": "Miles per gallon Imperial (MPG)",
