        </CardContent>

        <CardFooter className="pt-4 border-t bg-gray-50/50 dark:bg-gray-800/50">
          <div className="w-full text-center text-xs text-gray-500 dark:text-gray-400">
            <p>Common conversions: 1 kg/(m²·s) = 1,000 g/(m²·s) = 0.2048 lb/(ft²·s)</p>
          </div>
        </CardFooter>
      </Card>
    </div>
  );
}

