    setFromUnit(toUnit);
    setToUnit(temp);

    if (result) {
      setInputValue(result);
    }
  };

  const resetFields = () => {
    setInputValue("");
    setFromUnit("candela");
    setToUnit("lm/sr");
    setResult("");
