              id="currentAge"
              type="number"
              placeholder="Enter your current age"
              value={currentAge}
              onChange={(e) => setCurrentAge(e.target.value)}
              data-testid="input-current-age"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="retirementAge">Retirement Age</Label>
            <Input
              id="retirementAge"
              type="number"
              placeholder="Enter retirement age"
              value={retirementAge}
              onChange={(e) => setRetirementAge(e.target.value)}
              data-testid="input-retirement-age"
            />
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="space-y-2">
            <Label htmlFor="currentSavings">Current Savings ($)</Label>
            <Input
              id="currentSavings"
              type="number"
              placeholder="Enter current savings"
              value={currentSavings}
              onChange={(e) => setCurrentSavings(e.target.value)}
              data-testid="input-current-savings"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="monthlyContribution">Monthly Contribution ($)</Label>
            <Input
              id="monthlyContribution"
              type="number"
              placeholder="Enter monthly contribution"
              value={monthlyContribution}
              onChange={(e) => setMonthlyContribution(e.target.value)}
              data-testid="input-monthly-contribution"
            />
          </div>
        </div>

        <div className="space-y-2">
          <Label htmlFor="annualReturn">Expected Annual Return (%)</Label>
          <Input
            id="annualReturn"
            type="number"
            step="0.01"
            placeholder="Enter expected annual return"
            value={annualReturn}
            onChange={(e) => setAnnualReturn(e.target.value)}
            data-testid="input-annual-return"
          />
        </div>

        <div className="flex gap-4">
          <Button 
            onClick={calculateRetirement} 
